const NavSlide=()=>{const menuBtn=document.querySelector(".menu-btn"),nav=document.querySelector(".nav-links"),navLinks=document.querySelectorAll(".nav-links"),navSection=document.querySelector(".nvc"),socialMedia=document.querySelector(".Social-Mobile");menuBtn.addEventListener("click",()=>{nav.classList.toggle("nav-active"),navLinks.forEach((link,index)=>{link.style.animation?link.style.animation="":link.style.animation="navLinksFade 0.5s ease fowards ${index / 7 +1.5}s"})}),navSection.addEventListener("click",()=>{nav.classList.toggle("nav-active"),menuBtn.classList.toggle("open"),menuOpen=!1}),socialMedia.addEventListener("click",()=>{nav.classList.toggle("nav-active"),menuBtn.classList.toggle("open"),menuOpen=!1})};